import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import Sidebar from '../components/common/Sidebar';
import Header from '../components/common/Header';
import CalendarView from '../components/practitioner/CalendarView';
import PatientList from '../components/practitioner/PatientList';
import InsightsPanel from '../components/practitioner/InsightsPanel';
import { NAVIGATION_ITEMS } from '../constants';
import api from '../services/api';

const PractitionerDashboard = () => {
  const [activeItem, setActiveItem] = useState('dashboard');
  const [pendingAppointments, setPendingAppointments] = useState([]);
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    fetchPendingAppointments();
  }, []);

  const fetchPendingAppointments = async () => {
    try {
      const response = await api.get('/appointments/pending');
      setPendingAppointments(response.data);
    } catch (error) {
      console.error('Error fetching pending appointments:', error);
    }
  };

  const handleAcceptAppointment = async (appointmentId) => {
    setLoading(true);
    try {
      await api.patch(`/appointments/${appointmentId}/accept`);
      alert('Appointment accepted successfully!');
      fetchPendingAppointments();
    } catch (error) {
      alert('Error accepting appointment');
    } finally {
      setLoading(false);
    }
  };

  const handleRejectAppointment = async (appointmentId) => {
    const reason = prompt('Please provide a reason for rejection:');
    if (!reason) return;

    setLoading(true);
    try {
      await api.patch(`/appointments/${appointmentId}/reject`, { reason });
      alert('Appointment rejected successfully!');
      fetchPendingAppointments();
    } catch (error) {
      alert('Error rejecting appointment');
    } finally {
      setLoading(false);
    }
  };

  const handleLogout = () => {
    navigate('/login');
  };

  const renderContent = () => {
    switch (activeItem) {
      case 'dashboard':
        return (
          <div className="space-y-6">
            <div className="grid lg:grid-cols-4 gap-6">
              <div className="bg-gradient-to-r from-blue-500 to-blue-600 rounded-2xl p-6 text-white">
                <div className="flex items-center justify-between">
                  <div>
                    <p className="text-blue-100 text-sm">Today's Sessions</p>
                    <p className="text-3xl font-bold">8</p>
                    <p className="text-blue-100 text-sm">2 remaining</p>
                  </div>
                  <div className="text-4xl opacity-80">üìÖ</div>
                </div>
              </div>
              <div className="bg-gradient-to-r from-green-500 to-green-600 rounded-2xl p-6 text-white">
                <div className="flex items-center justify-between">
                  <div>
                    <p className="text-green-100 text-sm">Active Patients</p>
                    <p className="text-3xl font-bold">24</p>
                    <p className="text-green-100 text-sm">+3 this week</p>
                  </div>
                  <div className="text-4xl opacity-80">üë•</div>
                </div>
              </div>
              <div className="bg-gradient-to-r from-purple-500 to-purple-600 rounded-2xl p-6 text-white">
                <div className="flex items-center justify-between">
                  <div>
                    <p className="text-purple-100 text-sm">Avg Rating</p>
                    <p className="text-3xl font-bold">4.9</p>
                    <p className="text-purple-100 text-sm">‚≠ê‚≠ê‚≠ê‚≠ê‚≠ê</p>
                  </div>
                  <div className="text-4xl opacity-80">üèÜ</div>
                </div>
              </div>
              <div className="bg-gradient-to-r from-orange-500 to-orange-600 rounded-2xl p-6 text-white">
                <div className="flex items-center justify-between">
                  <div>
                    <p className="text-orange-100 text-sm">Revenue</p>
                    <p className="text-3xl font-bold">‚Çπ45K</p>
                    <p className="text-orange-100 text-sm">This month</p>
                  </div>
                  <div className="text-4xl opacity-80">üí∞</div>
                </div>
              </div>
            </div>
            <div className="grid lg:grid-cols-3 gap-6">
              <div className="lg:col-span-2">
                <CalendarView />
              </div>
              <div>
                <PatientList />
              </div>
            </div>
          </div>
        );
      case 'calendar':
        return <CalendarView />;
      case 'patients':
        return <PatientList />;
      case 'insights':
        return <InsightsPanel />;
      default:
        return <div className="card">Dashboard</div>;
    }
  };

  return (
    <div className="flex h-screen bg-gradient-to-br from-ayur-light/30 via-white to-ayur-accent/10">
      <Sidebar 
        items={NAVIGATION_ITEMS.PRACTITIONER}
        activeItem={activeItem}
        onItemClick={setActiveItem}
      />
      <div className="flex-1 flex flex-col min-w-0">
        <Header title="Practitioner Dashboard" userType="practitioner" onLogout={handleLogout} />
        <main className="flex-1 p-4 overflow-auto">
          <div className="max-w-full">
            <div className="mb-4">
              <h2 className="text-xl font-bold text-ayur-primary mb-1">Good morning, Dr. Sharma!</h2>
              <p className="text-sm text-gray-600">Manage your practice and help patients on their wellness journey</p>
            </div>
            {renderContent()}
          </div>
        </main>
      </div>
    </div>
  );
};

export default PractitionerDashboard;
